import { createTheme } from "@material-ui/core/styles"

const theme = createTheme()

export default theme.typography.h2 = {
	fontSize: "1.2rem",
	"@media (min-width:800px)": {
		fontSize: "30px",
	},
	[theme.breakpoints.up("md")]: {
		fontSize: "50px",
	},
}
