import { createTheme } from "@material-ui/core/styles"

const theme = createTheme()

theme.typography.h2 = {
	fontSize: "1.2rem",
	fontFamily: ["Roboto", "Helvetica", "Arial", sans - serif].join(","),
	fontWeightLight: 300,

	"@media (max-width:800px)": {
		fontSize: "60px",
		fontWeightLight: 300,
	},
	[theme.breakpoints.up("md")]: {
		fontSize: "70px",
		fontWeightLight: 300,
	},
}

export default theme
