import { createTheme } from "@material-ui/core/styles"

const theme = createTheme()

theme.typography.h2 = {
	fontSize: "1.2rem",
	fontFamily: "Roboto",
	"@media (max-width:800px)": {
		fontFamily: "Roboto",
		fontSize: "60px",
	},
	[theme.breakpoints.up("md")]: {
		fontSize: "70px",
	},
}

export default theme
