import { createTheme, responsiveFontSizes } from "@material-ui/core/styles"

const theme = createTheme()
theme.typography.h2 = {
	fontSize: "1.2rem",
	"@media (max-width:800px)": {
		fontSize: "1.5rem",
	},
	[theme.breakpoints.up("md")]: {
		fontSize: "2rem",
	},
}

export default theme
